package model;

public class Chofer {
	private String nombre;
	private int edad;

	public Chofer(String nombre, int edad) {
		// TODO Auto-generated constructor stub
		this.nombre = nombre;
		this.edad = edad;
	}

	public String getNombre() {
		// TODO Auto-generated method stub
		return nombre;
	}

	public int getEdad() {
		// TODO Auto-generated method stub
		return edad;
	}

}
